pragma solidity ^0.4.24;

contract KryptoKnight {
    
    struct Knight {
        uint Level;
        uint Experience;
        string Border;
    }
    
    struct Item {
        string Name;
        uint Quantity;
    }
    
    mapping(address => Knight) public players;
    
    mapping(address => Item[]) public itemsOfPlayers;
    
    uint totalKnightsInShop;
    
    constructor() public {
        addKnight(0xa05c526eae7859bb30070bdbaf03a03454e737a2);
        addKnight(0xc2d9cfff963c16015ca495050870a88796fd2610);
        totalKnightsInShop = 0;
    }
    
    function addKnight(address _addr) public {
        Knight memory knight = Knight({
           Level: 0,
           Experience: 0,
           Border: "Normal"
        });
        players[_addr] = knight;
    }
    
